CREATE TABLE IF NOT EXISTS department
(
    id bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 9223372036854775807 CACHE 1 ),
    description character varying(1000),
    name character varying(255),
    sequence_number integer,
    CONSTRAINT department_pkey PRIMARY KEY (id)
);


CREATE TABLE IF NOT EXISTS public.persons
(
    id bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 9223372036854775807 CACHE 1 ),
    blood_type character varying(255),
    email character varying(255) NOT NULL,
    first_name character varying(255) NOT NULL,
    gender character varying(255),
    last_name character varying(255) NOT NULL,
    phone_number character varying(255) NOT NULL,
    city character varying(255),
    country character varying(255),
    state character varying(255),
    street character varying(255),
    zip_code character varying(255),
    joining_date timestamp(6) without time zone,
    serial_number integer,
    department_id bigint NOT NULL,
    role character varying(255),
    CONSTRAINT persons_pkey PRIMARY KEY (id),
    CONSTRAINT "FK1413d4hgxnvjt28gegyp3rq4" FOREIGN KEY (department_id)
        REFERENCES public.department (id) MATCH SIMPLE
        ON UPDATE NO ACTION
        ON DELETE NO ACTION,
    CONSTRAINT persons_blood_type_check CHECK (blood_type::text = ANY (ARRAY['A_POSITIVE'::character varying, 'A_NEGATIVE'::character varying, 'B_POSITIVE'::character varying, 'B_NEGATIVE'::character varying, 'AB_POSITIVE'::character varying, 'AB_NEGATIVE'::character varying, 'O_POSITIVE'::character varying, 'O_NEGATIVE'::character varying]::text[])),
    CONSTRAINT persons_gender_check CHECK (gender::text = ANY (ARRAY['MALE'::character varying, 'FEMALE'::character varying]::text[])),
    CONSTRAINT persons_role_check CHECK (role::text = ANY (ARRAY['PRINCIPAL'::character varying, 'PROFESSOR'::character varying, 'ASSISTANT_PROFESSOR'::character varying, 'UNDERGRADUATE_STUDENT'::character varying, 'GRADUATE_STUDENT'::character varying, 'LECTURER'::character varying, 'RESEARCHER'::character varying, 'TEACHING_ASSISTANT'::character varying]::text[]))
);

